export default function Main() {
    const [censorType, setCensorType] = useState("")
    console.log("sensor type: " + censorType)

    function handleChange(event){
        console.log(event.target)
        setCensorType(event.target.value)
    }

    return (
        <form className="button-box">
           <div className="button-row">
               <div className="toggle-button">
                   <input
                        type="checkbox"
                        id="Black-Box"
                        name="censorType"
                        value="Black-Box"
                        checked={censorType === "Black-Box"}
                        onChange={handleChange}
                    />
                   <span className="toggle-slider"></span>
               </div>
               <span className="button-text"> 
                   Black-Box: Text  &#10145;
                   <span className="Black-Box">  Text</span>
               </span>
           </div>   
           {/* <input
                type="radio"
