import React from "react"

export default function ToggleButton(props){
    const {id, censorType, handleChange} = props
    let exampleText
    const setExampleText = () => {
        if (id === "Black-Box") {
            exampleText = <span style="background-color: black; color: black;">Text</span>
        } else if ( id === "Stars") {
            exampleText = <span>****</span>
        } else if( id === "Transparent") {
            exampleText = <span style="opacity: 0;">Text</span>
        } else if( id === "Strike") {
            exampleText = <span style="text-decoration: line-through;">Text</span>
        }
    }
    setExampleText();
    console.log(exampleText)
    return (
        <div className="button-row">
            <input
                className="toggle-button"
                type="checkbox"
                name="censor"
                id={id}
                value={id}
                checked={censorType === id}
                onChange={handleChange} 
            />
            <label
                className="toggle-label"
                htmlFor={id}
            >
                <span className="toggle-switch" />
            </label>
            <span className="toggle-description"> 
                {id}:  
                <span className={`censor-example ${id}`}>Text &#8594; ${exampleText}</span>
            </span> 
        </div>
    )
}
